import java.util.Scanner;

public class even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);

        int A =sc.nextInt();

        if(A % 2 == 0) {
        	System.out.println("even");
        } else {
        	System.out.println("not even");
        }
        
        
	}

}
